module.exports = {
  initial:'p2plex_to_hoster',
  states: {
    p2plex_to_hoster: {
      entry: ['p2plex_connect'],
      on: {
        RESOLVE: 'report',
        REJECT: 'fail_to_connect'
      }
    },
    receive_data: {
      entry: ['receive_data', 'verify'],
      exit: ['disconnect'],
      on: {
        RESOLVE: 'report',
        REJECT: 'fail_to_receive_data'
      }
    },
    report: {
      entry: ['proof_of_storage_report'],
      on: {
        _RESOLVE: '#idleState',
        _REJECT: 'fail_to_report'
      }
    },
    fail_to_report: {
      on: {
        RETRY: 'report'
      }
    },
    fail_to_receive_data: {
      on: {
        RETRY: 'receive_data'
      }
    },
    fail_to_connect: {
      on: {
        RETRY: 'p2plex_to_hoster'
      }
    },
  }
}
