const XState = require('xstate')
const { Machine, assign, spawn, send, sendParent } = XState

const retryLessThan3 = (context, event) => context.retry < 3
const incrementRetry = (context) => { return { ...context, retry: context.retry + 1 } }
const resetRetry = (context, event) => { 
  console.log('attest-storage-sm:', event.data)
  return { ...context, retry: 0 } 
}

module.exports = Machine({
  initial: 'connecting',
  context: {
    retry: 0
  },
  states: {
    connecting: {
      invoke: {
        id: 'connect', src: 'connect_to_hoster',
        onDone: {
          target: 'verifying',
          actions: assign(resetRetry),
        },
        onError: {
          target: 'connecting', 
          cond: retryLessThan3,
          target: 'reporting',
          actions: assign(incrementRetry),
        }
      },
    },
    verifying: {
      invoke: {
        id: 'verifying', src: 'get_and_verify_data',
        onDone: {
          target: 'reporting',
          actions: assign(resetRetry),
        },
        onError: {
          target: 'verifying',
          cond: retryLessThan3,
          target: 'reporting',
          actions: assign(incrementRetry),
        }      
      }
    },
    reporting: {
      invoke: {
        id: 'reporting', src: 'send_report_to_chain',
        onDone: {
          target: 'disconnecting',
          actions: assign(resetRetry),
        },
        onError: {
          target: 'reporting',
          cond: retryLessThan3,
          target: 'finish',
          actions: assign(incrementRetry),
        }
      }
    },
    disconnecting: {
      invoke: {
        id: 'disconnect', src: 'disconnect',
        onDone: {
          target: 'finish',
          actions: assign(resetRetry),
        },
        onError: {
          target: 'disconnecting',
          cond: retryLessThan3,
          target: 'reporting',
          actions: assign(incrementRetry),
        }
      },
    },
    finish: {
      type: 'final',
      data: {type: 'finished'}
    },
  }
},{
  services: {
    connect_to_hoster: new Promise((resolve, reject) => { setTimeout(() => { resolve('reported') }, 3000) }),
    get_and_verify_data: new Promise((resolve, reject) => { setTimeout(() => { resolve('reported') }, 3000) }),
    send_report_to_chain: new Promise((resolve, reject) => { setTimeout(() => { resolve('reported') }, 3000) }),
    disconnect: new Promise((resolve, reject) => { setTimeout(() => { resolve('connected') }, 3000) }),
  }
})