const XState = require('xstate')
const { Machine, assign, spawn, send, sendParent } = XState

const encode = require('encode-hosting-setup')
const host = require('host-hosting-setup')
const attest = require('attest-hosting-setup')


module.exports = Machine({
  id: 'datdot-service',
  initial: 'idle',
  context: {},
  states: {
    idle: {
      on: {
        SETUP: {
          actions: assign({
            ref: (context, event) => [
              { name: event.id, ref: spawn(attest, `${event.role}-${event.id}`, { sync: true }) }
            ]
          })
        },
        RESOLVE: {
          actions: send('RESOLVE', {
            to: (context, event) => `${event.role}-${event.id}`
          })
        },
        REJECT: {
          actions: send('REJECT', {
            to: (context, event) => event.id
          })
        },
        onDone: {
          // actions: () => console.log('done')
        },
        HOSTING_REPAIR: {
          target: ['attest']
        },
        // EVENT_PROOF_OF_STORAGE_REQUEST: {
        //   target: 'proof_of_storage'
        // },
        // EVENT_PERFORMANCE_BENCHMARK: {
        //   target: 'performance_benchmark'
        // },
        // EVENT_HOSTING_PAUSE: {
        //   target: 'hosting_pause'
        // },
        // EVENT_HOSTING_END: {
        //   target: 'hosting_end'
        // },
      },
    },
    // host: {
    //   on: {},
    //   ...host,
    // },
    encode: {
      on: {},
      ...encode,
    },
    attest: {
      on: {},
      ...attest,
    },
  }
  })
