const XState = require('xstate')
const { Machine } = XState
const hosting_start = require('./sub-hosting-start')

module.exports = Machine({
  initial: 'idle',
  states: {
    idle: {
      id: 'idleState',
      on: {
        EVENT_HOSTING_START: {
          target: 'hosting_start'
        },
        EVENT_HOSTING_REPAIR: { // new_hosting_setup
          target: 'hosting_start'
        },
      },
    },
    hosting_start: {
      on: {},
      ...hosting_start
    },
  }
})
