module.exports = {
    initial: 'hosting_repair',
    states: {
      hosting_repair: {
        entry: ['resume'], // streaming, all happening in parallel
        exit: ['disconnect'],
        on: {
          _RESOLVE: '#idleState',
          _REJECT: 'failure_hosting_repair'  // retry
        }
      },
      failure_hosting_repair: {
        on: {
          RETRY: 'hosting_repair'
        }
      },
    }
}
